class MoodEditor  {
    mood;
    container;
    happyImg;
    sadImg;

    onKeyDown(event) {
        var key = event.key;
        if (
            key === 'ArrowLeft' || // left
            key == 'ArrowRight'
        ) {
            // right
            this.toggleMood();
            event.stopPropagation();
        }
    }

    toggleMood() {
        this.selectMood(this.mood === 'Happy' ? 'Sad' : 'Happy');
    }

    init(params) {
        this.container = document.createElement('div');
        this.container.className = 'mood';
        this.container.tabIndex = '0'; // to allow the div to capture events

        this.happyImg = document.createElement('img');
        this.happyImg.src = 'https://www.ag-grid.com/example-assets/smileys/happy.png';

        this.sadImg = document.createElement('img');
        this.sadImg.src = 'https://www.ag-grid.com/example-assets/smileys/sad.png';

        this.container.appendChild(this.happyImg);
        this.container.appendChild(this.sadImg);

        this.happyImg.addEventListener('click', () => {
            this.selectMood('Happy');
            params.stopEditing();
        });
        this.sadImg.addEventListener('click', () => {
            this.selectMood('Sad');
            params.stopEditing();
        });
        this.container.addEventListener('keydown', (event) => {
            this.onKeyDown(event);
        });

        this.selectMood(params.value);
    }

    selectMood(mood) {
        this.mood = mood;
        this.happyImg.className = mood === 'Happy' ? 'selected' : 'default';
        this.sadImg.className = mood === 'Sad' ? 'selected' : 'default';
    }

    // gets called once when grid ready to insert the element
    getGui() {
        return this.container;
    }

    afterGuiAttached() {
        this.container.focus();
    }

    getValue() {
        return this.mood;
    }

    // any cleanup we need to be done here
    destroy() {}
}

files.numericCellEditor.js:
// backspace starts the editor on Windows
const KEY_BACKSPACE = 'Backspace';
class NumericCellEditor  {
    eInput;
    cancelBeforeStart;

    // gets called once before the renderer is used
    init(params) {
        // create the cell
        this.eInput = document.createElement('input');
        this.eInput.className = 'simple-input-editor';

        const eventKey = params.eventKey;

        if (eventKey === KEY_BACKSPACE) {
            this.eInput.value = '';
        } else if (this.isCharNumeric(eventKey)) {
            this.eInput.value = eventKey;
        } else {
            if (params.value !== undefined && params.value !== null) {
                this.eInput.value = params.value;
            }
        }

        this.eInput.addEventListener('keydown', (event) => {
            if (!event.key || event.key.length !== 1) {
                return;
            }

            if (!this.isNumericKey(event)) {
                this.eInput.focus();
                if (event.preventDefault) event.preventDefault();
            } else if (this.isNavigationKey(event) || this.isBackspace(event)) {
                event.stopPropagation();
            }
        });

        // only start edit if key pressed is a number, not a letter
        const isCharacter = eventKey && eventKey.length === 1;
        var isNotANumber = isCharacter && '1234567890'.indexOf(eventKey) < 0;
        this.cancelBeforeStart = !!isNotANumber;
    }

    isNumericKey(event) {
        const charStr = event.key;
        return this.isCharNumeric(charStr);
    }

    isBackspace(event) {
        return event.key === KEY_BACKSPACE;
    }

    isNavigationKey(event) {
        return event.key === 'ArrowLeft' || event.key === 'ArrowRight';
    }

    isCharNumeric(charStr) {
        return charStr && !!/^\d+$/.test(charStr);
    }

    // gets called once when grid ready to insert the element
    getGui() {
        return this.eInput;
    }

    // focus and select can be done after the gui is attached
    afterGuiAttached() {
        this.eInput.focus();
    }

    // returns the new value after editing
    isCancelBeforeStart() {
        return this.cancelBeforeStart;
    }

    // example - will reject the number if it contains the value 007
    // - not very practical, but demonstrates the method.
    isCancelAfterEnd() {
        var value = this.getValue();
        return value.indexOf('007') >= 0;
    }

    // returns the new value after editing
    getValue() {
        return this.eInput.value;
    }

    // any cleanup we need to be done here
    destroy() {
        // but this example is simple, no cleanup, we could  even leave this method out as it's optional
    }
}

files.main.js:
let gridApi;

const gridOptions = {
    columnDefs: [
        { field: 'type' },
        {
            field: 'value',
            editable: true,
            cellEditorSelector: cellEditorSelector,
        },
    ],
    defaultColDef: {
        flex: 1,
        cellDataType: false,
    },
    rowData: getData(),

    onRowEditingStarted: onRowEditingStarted,
    onRowEditingStopped: onRowEditingStopped,
    onCellEditingStarted: onCellEditingStarted,
    onCellEditingStopped: onCellEditingStopped,
};

function onRowEditingStarted(event) {
    console.log('never called - not doing row editing');
}

function onRowEditingStopped(event) {
    console.log('never called - not doing row editing');
}

function onCellEditingStarted(event) {
    console.log('cellEditingStarted');
}

function onCellEditingStopped(event) {
    console.log('cellEditingStopped');
}

function cellEditorSelector(params) {
    if (params.data.type === 'age') {
        return {
            component: NumericCellEditor,
        };
    }

    if (params.data.type === 'gender') {
        return {
            component: 'agRichSelectCellEditor',
            params: {
                values: ['Male', 'Female'],
            },
        };
    }

    if (params.data.type === 'mood') {
        return {
            component: MoodEditor,
            popup: true,
            popupPosition: 'under',
        };
    }

    return undefined;
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 
});
