import { AgGridAngular } from '@ag-grid-community/angular';
import { ClientSideRowModelModule } from '@ag-grid-community/client-side-row-model';
import { ColDef, GridApi, GridReadyEvent, IRowNode } from '@ag-grid-community/core';
import { ModuleRegistry } from '@ag-grid-community/core';
import '@ag-grid-community/styles/ag-grid.css';
import '@ag-grid-community/styles/ag-theme-quartz.css';
import { Component } from '@angular/core';

import { ChildMessageRenderer } from './child-message-renderer.component';
import { CubeRenderer } from './cube-renderer.component';
import { CurrencyRenderer } from './currency-renderer.component';
import { ParamsRenderer } from './params-renderer.component';
import { SquareRenderer } from './square-renderer.component';
import './styles.css';

ModuleRegistry.registerModules([ClientSideRowModelModule]);

@Component({
    selector: 'my-app',
    standalone: true,
    imports: [AgGridAngular, ChildMessageRenderer, CubeRenderer, CurrencyRenderer, ParamsRenderer, SquareRenderer],
    template: ` <div class="example-wrapper">
        <button (click)="refreshEvenRowsCurrencyData()" style="margin-bottom: 10px" class="btn btn-primary">
            Refresh Even Row Currency Data
        </button>
        <ag-grid-angular
            #agGrid
            style="width: 100%; height: 100%;"
            id="myGrid"
            [class]="themeClass"
            [columnDefs]="columnDefs"
            [rowData]="rowData"
            [context]="context"
            [defaultColDef]="defaultColDef"
            (gridReady)="onGridReady($event)"
        />
    </div>`,
})
export class AppComponent {
    themeClass =
        /** DARK MODE START **/ document.documentElement?.dataset.defaultTheme ||
        'ag-theme-quartz' /** DARK MODE END **/;
    private gridApi!: GridApi;

    public columnDefs: ColDef[] = [
        {
            headerName: 'Row',
            field: 'row',
            width: 150,
        },
        {
            headerName: 'Square',
            field: 'value',
            cellRenderer: SquareRenderer,
            editable: true,
            colId: 'square',
            width: 150,
        },
        {
            headerName: 'Cube',
            field: 'value',
            cellRenderer: CubeRenderer,
            colId: 'cube',
            width: 150,
        },
        {
            headerName: 'Row Params',
            field: 'row',
            cellRenderer: ParamsRenderer,
            colId: 'params',
            width: 150,
        },
        {
            headerName: 'Currency (Pipe)',
            field: 'currency',
            cellRenderer: CurrencyRenderer,
            colId: 'currency',
            width: 120,
        },
        {
            headerName: 'Child/Parent',
            field: 'value',
            cellRenderer: ChildMessageRenderer,
            colId: 'params',
            editable: false,
            minWidth: 150,
        },
    ];

    public defaultColDef: ColDef = {
        editable: true,
        flex: 1,
        minWidth: 100,
        filter: true,
    };

    public rowData: any[];
    public context: any;

    constructor() {
        this.rowData = this.createRowData();
        this.context = { componentParent: this };
    }

    refreshEvenRowsCurrencyData() {
        this.gridApi.forEachNode((rowNode: IRowNode) => {
            if (rowNode.data.value % 2 === 0) {
                rowNode.setDataValue('currency', rowNode.data.value + Number(Math.random().toFixed(2)));
            }
        });
        this.gridApi.refreshCells({ columns: ['currency'] });
    }

    onGridReady(params: GridReadyEvent) {
        this.gridApi = params.api;
    }

    methodFromParent(cell: any) {
        alert('Parent Component Method from ' + cell + '!');
    }

    createRowData() {
        const rowData = [];
        for (let i = 0; i < 15; i++) {
            rowData.push({
                row: 'Row ' + i,
                value: i,
                currency: i + Number(Math.random().toFixed(2)),
            });
        }
        return rowData;
    }
}

