class NumberFilterComponent  {
    filterText;
    filterParams;
    gui;
    eFilterText;
    onFilterChanged;

    init(params) {
        this.filterText = null;
        this.filterParams = params;
        this.setupGui();
    }

    // not called by AG Grid, just for us to help setup
    setupGui() {
        this.gui = document.createElement('div');
        this.gui.innerHTML = `
            <div style="padding: 4px">
                <div style="font-weight: bold;">Greater than: </div>
                <div>
                    <input style="margin: 4px 0 4px 0;" type="number" min="0" id="filterText" placeholder="Number of medals..."/>
                </div>
            </div>
        `;

        this.onFilterChanged = () => {
            this.extractFilterText();
            this.filterParams.filterChangedCallback();
        };

        this.eFilterText = this.gui.querySelector('#filterText');
        this.eFilterText.addEventListener('input', this.onFilterChanged);
    }

    isNumeric = (n) => !isNaN(parseFloat(n)) && isFinite(parseFloat(n));

    extractFilterText() {
        this.filterText = this.eFilterText.value;
    }

    getGui() {
        return this.gui;
    }

    doesFilterPass(params) {
        if (!this.isFilterActive()) {
            return false;
        }

        const { node } = params;

        const value = this.filterParams.getValue(node);

        const filterValue = this.filterText;

        if (value == null) return false;
        return Number(value) > Number(filterValue);
    }

    isFilterActive() {
        return (
            this.filterText !== null &&
            this.filterText !== undefined &&
            this.filterText !== '' &&
            this.isNumeric(this.filterText)
        );
    }

    getModel() {
        return this.isFilterActive() ? Number(this.eFilterText.value) : null;
    }

    setModel(model) {
        this.eFilterText.value = model;
        this.extractFilterText();
    }

    destroy() {
        this.eFilterText.removeEventListener('input', this.onFilterChanged);
    }

    getModelAsString() {
        return this.isFilterActive() ? '>' + this.filterText : '';
    }
}

