import 'ag-grid-community/styles/ag-grid.css';
import "ag-grid-community/styles/ag-theme-quartz.css";
import { CellValueChangedEvent, ColDef, ColGroupDef, GridApi, GridOptions, SideBarDef, createGrid } from 'ag-grid-community';
import { CustomStatsToolPanel } from './customStatsToolPanel';
import 'ag-grid-charts-enterprise';
import { IOlympicData } from './interfaces'




const columnDefs: ColDef[] = [
    { field: 'athlete', width: 150, filter: 'agTextColumnFilter' },
    { field: 'age', width: 90 },
    { field: 'country', width: 120 },
    { field: 'year', width: 90 },
    { field: 'date', width: 110 },
    { field: 'gold', width: 100, filter: false },
    { field: 'silver', width: 100, filter: false },
    { field: 'bronze', width: 100, filter: false },
    { field: 'total', width: 100, filter: false },
];

let gridApi: GridApi<IOlympicData>;

const gridOptions: GridOptions<IOlympicData> = {
    defaultColDef: {
        editable: true,
        flex: 1,
        minWidth: 100,
        filter: true,
    },
    icons: {
        'custom-stats': '<span class="ag-icon ag-icon-custom-stats"></span>',
    },
    columnDefs: columnDefs,
    sideBar: {
        toolPanels: [
            {
                id: 'columns',
                labelDefault: 'Columns',
                labelKey: 'columns',
                iconKey: 'columns',
                toolPanel: 'agColumnsToolPanel',
            },
            {
                id: 'filters',
                labelDefault: 'Filters',
                labelKey: 'filters',
                iconKey: 'filter',
                toolPanel: 'agFiltersToolPanel',
            },
            {
                id: 'customStats',
                labelDefault: 'Custom Stats',
                labelKey: 'customStats',
                iconKey: 'custom-stats',
                toolPanel: CustomStatsToolPanel,
                toolPanelParams: {
                    title: 'Custom Stats',
                },
            },
        ],
        defaultToolPanel: 'customStats',
    },
    onCellValueChanged: (params: CellValueChangedEvent) => {
        params.api.refreshClientSideRowModel();
    },
};

// setup the grid after the page has finished loading
    const gridDiv = document.querySelector<HTMLElement>('#myGrid')!;
    gridApi = createGrid(gridDiv, gridOptions);

    fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
        .then((response) => response.json())
        .then((data) => {
            gridApi!.setGridOption('rowData', data);
        });;

