var LAPTOPS = ['Hewlett Packard', 'Lenovo', 'Dell', 'Asus', 'Apple', 'Acer', 'Microsoft', 'Razer'];

let idCounter = 0;

function letter(i) {
    return 'abcdefghijklmnopqrstuvwxyz'.substring(i, i + 1);
}

function randomLetter() {
    return letter(Math.floor(Math.random() * 26 + 1));
}

function getData() {
    const myRowData = [];
    for (let i = 0; i < 20; i++) {
        const name = 'Mr ' + randomLetter().toUpperCase();
        const fixed = Boolean(Math.round(Math.random()));
        const laptop = LAPTOPS[i % LAPTOPS.length];
        const value = Math.floor(Math.random() * 100) + 10; // between 10 and 110

        myRowData.push(createDataItem(name, laptop, fixed, value));
    }
    return myRowData;
}

function createDataItem(
    name,
    laptop,
    fixed,
    value,
    idToUse = undefined
) {
    const id = idToUse != null ? idToUse : idCounter++;
    return {
        id: id,
        name: name,
        fixed: fixed,
        laptop: laptop,
        value: value,
    };
}

files.main.js:
function getRowId(params) {
    return String(params.data.id);
}

let gridApi;
const columnDefs = [
    { field: 'name' },
    { field: 'laptop' },
    {
        field: 'fixed',
        enableCellChangeFlash: true,
    },
    {
        field: 'value',
        enableCellChangeFlash: true,
        sort: 'desc',
    },
];

function onBtnApply() {
    const updatedItems = [];
    gridApi.forEachNode((rowNode) => {
        const newValue = Math.floor(Math.random() * 100) + 10;
        const newBoolean = Boolean(Math.round(Math.random()));
        const newItem = createDataItem(rowNode.data.name, rowNode.data.laptop, newBoolean, newValue, rowNode.data.id);
        updatedItems.push(newItem);
    });

    gridApi.applyTransaction({ update: updatedItems });
}

function onBtnRefreshModel() {
    gridApi.refreshClientSideRowModel('filter');
}

const gridOptions = {
    columnDefs: columnDefs,
    defaultColDef: {
        flex: 1,
        filter: true,
        floatingFilter: true,
    },
    getRowId: getRowId,
    suppressModelUpdateAfterUpdateTransaction: true,
    onGridReady: (params) => {
        params.api
            .setColumnFilterModel('fixed', {
                filterType: 'set',
                values: ['true'],
            })
            .then(() => {
                gridApi.onFilterChanged();
            });
        params.api.setGridOption('rowData', getData());
    },
};

// wait for the document to be loaded, otherwise
// AG Grid will not find the div in the document.
document.addEventListener('DOMContentLoaded', function () {
    const eGridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(eGridDiv, gridOptions); 
});
