const createBlob = (response) => response.blob();

const fromBlobToBase64 = (blob) =>
    new Promise((res) => {
        const reader = new FileReader();
        reader.onloadend = () => res(reader.result);
        reader.readAsDataURL(blob);
    });

const createBase64ImageFromURL = (url) =>
    fetch(url)
        .then(createBlob)
        .then((blob) => fromBlobToBase64(blob));

const createBase64ImagesFromURLArray = (arr) => {
    const map = new Map();
    const promiseArray = arr.reduce((promises, currentUrl) => {
        if (!map.has(currentUrl)) {
            const promise = createBase64ImageFromURL(currentUrl);
            promise.then((base64) => map.set(currentUrl, base64));
            promises.push(promise);
        }

        return promises;
    }, []);

    return Promise.all(promiseArray).then(() => map);
};

// Example specific code
const createCountryCodeMap = (countryCodeObject) =>
    fetch('https://flagcdn.com/en/codes.json')
        .then((response) => response.json())
        .then((codes) => {
            Object.keys(codes).forEach((code) => (countryCodeObject[codes[code]] = code));
            return countryCodeObject;
        });

const createBase64FlagsFromResponse = (response, countryCodeObject, base64FlagsObject) => {
    const urlPrefix = 'https://flagcdn.com/w20/';
    const extension = '.png';
    return response
        .json()
        .then((data) => createCountryCodeMap(countryCodeObject).then(() => data.filter((rec) => rec.country != null)))
        .then((data) => {
            const urls = data.map((rec) => {
                const countryCode = countryCodeObject[rec.country];
                return urlPrefix + countryCode + extension;
            });

            return createBase64ImagesFromURLArray(urls).then((map) => {
                map.forEach((value, key) => {
                    const code = key.replace(urlPrefix, '').replace(extension, '');
                    base64FlagsObject[code] = value;
                });
                return data;
            });
        })
        .then((data) => data);
};

files.countryCellRenderer.js:
class CountryCellRenderer  {
    eGui;

    init(params) {
        const { data } = params;
        const { country } = data;

        this.eGui = document.createElement('div');
        const img = document.createElement('img');
        const text = document.createTextNode(` ${country}`);
        img.alt = country;

        const context = params.context;
        img.src = context.base64flags[context.countryCodes[country]];
        this.eGui.appendChild(img);
        this.eGui.appendChild(text);
    }

    getGui() {
        return this.eGui;
    }

    refresh() {
        return false;
    }
}

files.main.js:
const countryCodes = {};
const base64flags = {};

const columnDefs = [
    { field: 'athlete', width: 200 },
    {
        field: 'country',
        cellClass: 'countryCell',
        cellRenderer: CountryCellRenderer,
    },
    { field: 'age' },
    { field: 'year' },
    { field: 'date' },
    { field: 'sport' },
    { field: 'gold' },
    { field: 'silver' },
    { field: 'bronze' },
    { field: 'total' },
];

let gridApi;

const gridOptions = {
    columnDefs: columnDefs,
    defaultColDef: {
        width: 150,
    },
    excelStyles: [
        {
            id: 'countryCell',
            alignment: {
                vertical: 'Center',
                indent: 4,
            },
        },
    ],
    defaultExcelExportParams: {
        addImageToCell: (rowIndex, col, value) => {
            if (col.getColId() !== 'country') {
                return;
            }

            const countryCode = countryCodes[value];
            return {
                image: {
                    id: countryCode,
                    base64: base64flags[countryCode],
                    imageType: 'png',
                    width: 20,
                    height: 11,
                    position: {
                        offsetX: 10,
                        offsetY: 5.5,
                    },
                },
                value,
            };
        },
    },
    context: {
        base64flags: base64flags,
        countryCodes: countryCodes,
    } ,
    onGridReady: (params) => {
        fetch('https://www.ag-grid.com/example-assets/small-olympic-winners.json')
            .then((data) => createBase64FlagsFromResponse(data, countryCodes, base64flags))
            .then((data) => params.api.setGridOption('rowData', data));
    },
};

function onBtExport() {
    gridApi.exportDataAsExcel();
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    const gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 
});

