files.data.js:
var animals = ['Monkey', 'Lion', 'Elephant', 'Tiger', 'Giraffe', 'Antelope'];

function getData() {
    var rows = [];

    for (var i = 0; i < 2000; i++) {
        var index = Math.floor(Math.random() * animals.length);
        rows.push({ animal: animals[index] });
    }

    return rows;
}

files.main.js:
var list1 = ['Elephant', 'Lion', 'Monkey'];
var list2 = ['Elephant', 'Giraffe', 'Tiger'];

var valuesArray = list1.slice();
var valuesCallbackList = list1;

function valuesCallback(params) {
    setTimeout(() => {
        params.success(valuesCallbackList);
    }, 1000);
}

var arrayFilterParams = {
    values: valuesArray,
};

var callbackFilterParams = {
    values: valuesCallback,
    refreshValuesOnOpen: true,
};

let gridApi;

const gridOptions = {
    columnDefs: [
        {
            colId: 'array',
            headerName: 'Values Array',
            field: 'animal',
            filter: 'agSetColumnFilter',
            filterParams: arrayFilterParams,
        },
        {
            colId: 'callback',
            headerName: 'Values Callback',
            field: 'animal',
            filter: 'agSetColumnFilter',
            filterParams: callbackFilterParams,
        },
    ],
    defaultColDef: {
        flex: 1,
        filter: true,
    },
    sideBar: 'filters',
    rowData: getData(),
    onFirstDataRendered: onFirstDataRendered,
};

function onFirstDataRendered(params) {
    params.api.getToolPanelInstance('filters').expandFilters();
}

function useList1() {
    console.log('Updating values to ' + list1);
    valuesArray.length = 0;
    list1.forEach((value) => {
        valuesArray.push(value);
    });

    gridApi.getColumnFilterInstance('array').then((filter) => {
        filter.refreshFilterValues();

        valuesCallbackList = list1;
    });
}

function useList2() {
    console.log('Updating values to ' + list2);
    valuesArray.length = 0;
    list2.forEach((value) => {
        valuesArray.push(value);
    });

    gridApi.getColumnFilterInstance('array').then((filter) => {
        filter.refreshFilterValues();

        valuesCallbackList = list2;
    });
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 
});
