let gridApi;

const gridOptions = {
    columnDefs: [
        { field: 'athlete', minWidth: 200 },
        { field: 'gold' },
        { field: 'silver' },
        { field: 'bronze' },
        { field: 'total' },
        { field: 'age' },
        {
            field: 'country',
            rowGroup: true,
            hide: true,
            valueFormatter: countryValueFormatter,
            valueGetter: countryValueGetter,
            keyCreator: countryKeyCreator,
        },
        { field: 'year' },
        { field: 'date' },
        { field: 'sport', minWidth: 200 },
    ],
    defaultColDef: {
        flex: 1,
        minWidth: 150,
    },
    autoGroupColumnDef: {
        minWidth: 200,
    },
};

function countryValueFormatter(params) {
    if (!params.value) {
        return '';
    }
    return `[${params.value.code}] ${params.value.name}`;
}

function countryKeyCreator(params) {
    var countryObject = params.value;
    return countryObject.name;
}

function countryValueGetter(params) {
    // hack the data  - replace the country with an object of country name and code
    var countryName = params.data.country;
    var countryCode = countryName.substring(0, 2).toUpperCase();
    return {
        name: countryName,
        code: countryCode,
    };
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 

    fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
        .then((response) => response.json())
        .then((data) => gridApi.setGridOption('rowData', data));
});
