    { field: 'jan' },
    { field: 'feb' },
    { field: 'mar' },
    { field: 'apr' },
    { field: 'may' },
    { field: 'jun' },
    { field: 'jul' },
    { field: 'aug' },
    { field: 'sep' },
    { field: 'oct' },
    { field: 'nov' },
    { field: 'dec' },
];

const waterfallColIds = ['financials', 'amount'];

const waterfallColDefs = [
    { field: 'financials', width: 150, chartDataType: 'category' },
    { field: 'amount', chartDataType: 'series' },
];



const GridExample = () => {
    const gridRef = useRef();
    const containerStyle = useMemo(() => ({ width: '100%', height: '100%' }), []);
    const gridStyle = useMemo(() => ({height: '100%', width: '100%'}), []);

    const [columnDefs, setColumnDefs] = useState(heatmapColDefs);
    const defaultColDef = useMemo(() => { return {
    flex: 1,
    minWidth: 100,
} }, []);
    const popupParent = useMemo(() => { return document.body }, []);
    const chartToolPanelsDef = useMemo(() => { return {
    defaultToolPanel: 'settings',
} }, []);


            const onGridReady = useCallback((params) => {
                /** DARK INTEGRATED START **/
    const isInitialModeDark = () => {
            const attr = document.documentElement.getAttribute('data-default-theme');
            return attr ? attr.endsWith('-dark') : false;
        };

        const updateChartThemes = (isDark) => {           
            const themes = ['ag-default', 'ag-material', 'ag-sheets', 'ag-polychroma', 'ag-vivid'];            
            const currentThemes = params.api.getGridOption('chartThemes');                    
            const customTheme = currentThemes && currentThemes.some(theme => theme.startsWith('my-custom-theme'));

            let modifiedThemes = customTheme
                ? (isDark ? ['my-custom-theme-dark', 'my-custom-theme-light'] : ['my-custom-theme-light', 'my-custom-theme-dark'])
                : Array.from(new Set(themes.map((theme) => theme + (isDark ? '-dark' : ''))));                      

            // updating the 'chartThemes' grid option will cause the chart to reactively update!
            params.api.setGridOption('chartThemes', modifiedThemes);
        };

        // update chart themes when example first loads
        updateChartThemes(isInitialModeDark());

        const handleColorSchemeChange = (event) => {
            const { darkMode } = event.detail;
            updateChartThemes(darkMode);
        }

        // listen for user-triggered dark mode changes (not removing listener is fine here!)
        document.addEventListener('color-scheme-change', handleColorSchemeChange);
    /** DARK INTEGRATED END **/

    getData('heatmap').then((rowData) => params.api.setGridOption('rowData', rowData));

            }, []);

const onFirstDataRendered = useCallback((params) => {
    chartRef = params.api.createRangeChart({
        chartContainer: document.querySelector('#myChart'),
        chartType: 'heatmap',
        cellRange: {
            columns: heatmapColIds,
        },
    });
}, [heatmapColIds])

   const updateChart = useCallback((chartType) => {
    getData(chartType).then((rowData) => {
        gridRef.current.api.updateGridOptions({
            columnDefs: chartType === 'heatmap' ? heatmapColDefs : waterfallColDefs,
            rowData,
        });
        gridRef.current.api.updateChart({
            type: 'rangeChartUpdate',
            chartId: chartRef.chartId,
            chartType,
            cellRange: {
                columns: chartType === 'heatmap' ? heatmapColIds : waterfallColIds,
            },
        });
    });
}, [getData,heatmapColDefs,waterfallColDefs,chartRef,heatmapColIds,waterfallColIds])

    return  (
            <div style={containerStyle}>
                <div className="wrapper">
    <div className="button-container">
        <button onClick={() => updateChart('heatmap')}>Heatmap</button>
        <button onClick={() => updateChart('waterfall')}>Waterfall</button>
    </div>

        <div  style={gridStyle} className={/** DARK MODE START **/document.documentElement.dataset.defaultTheme || 'ag-theme-quartz'/** DARK MODE END **/}>
            <AgGridReact
                ref={gridRef}

columnDefs={columnDefs}
defaultColDef={defaultColDef}
popupParent={popupParent}
enableRangeSelection={true}
enableCharts={true}
chartToolPanelsDef={chartToolPanelsDef}
onGridReady={onGridReady}
onFirstDataRendered={onFirstDataRendered}
            />
        </div>
    <div id="myChart" className="ag-theme-quartz"></div>
</div>

            </div>
        );

}

const root = createRoot(document.getElementById('root'));
root.render(<StrictMode><GridExample /></StrictMode>);
window.tearDownExample = () => root.unmount();

