files.style.css:
.chart-wrapper {
    display: flex;
    flex-direction: column;
    height: 410px;
    overflow: hidden;
    margin-top: 0.5rem;
    border: 1px solid var(--ag-border-color);
}

.chart-wrapper-top {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
}

.chart-wrapper-body {
    border-top: 1px solid var(--ag-border-color);
    height: 100%;
}

files.main.js:
let gridApi;

const gridOptions = {
    columnDefs: [
        { field: 'athlete', width: 150, chartDataType: 'category' },
        { field: 'gold', chartDataType: 'series' },
        { field: 'silver', chartDataType: 'series' },
        { field: 'bronze', chartDataType: 'series' },
        { field: 'total', chartDataType: 'series' },
    ],
    defaultColDef: { flex: 1 },
    enableRangeSelection: true,
    enableCharts: true,
    popupParent: document.body,
    createChartContainer,
};

// Function for creating the chart container
function createChartContainer(chartRef) {
    const eChart = chartRef.chartElement;
    const themeName = document.documentElement?.getAttribute('data-default-theme') || 'ag-theme-quartz';
    const eParent = document.querySelector('#container') ;

    const chartWrapperHTML = `
    <div class="chart-wrapper ${themeName}">
      <div class="chart-wrapper-top">
        <h2 class="chart-wrapper-title">Chart created ${getFormattedDate()}</h2>
        <button class="chart-wrapper-close">Destroy Chart</button>
      </div>
      <div class="chart-wrapper-body"></div>
    </div>
  `;

    eParent.insertAdjacentHTML('beforeend', chartWrapperHTML);
    const eChartWrapper = eParent.lastElementChild ;

    eChartWrapper.querySelector('.chart-wrapper-body').appendChild(eChart);
    eChartWrapper.querySelector('.chart-wrapper-close').addEventListener('click', () => {
        chartRef.destroyChart();
        eParent.removeChild(eChartWrapper);
    });
}

function getFormattedDate() {
    return new Intl.DateTimeFormat('en', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric',
        hour12: true,
        timeZone: 'UTC',
    }).format(new Date());
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    const gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); /** DARK INTEGRATED START **/
    const isInitialModeDark = () => {
            const attr = document.documentElement.getAttribute('data-default-theme');
            return attr ? attr.endsWith('-dark') : false;
        };

        const updateChartThemes = (isDark) => {           
            const themes = ['ag-default', 'ag-material', 'ag-sheets', 'ag-polychroma', 'ag-vivid'];            
            const currentThemes = gridApi.getGridOption('chartThemes');                    
            const customTheme = currentThemes && currentThemes.some(theme => theme.startsWith('my-custom-theme'));

            let modifiedThemes = customTheme
                ? (isDark ? ['my-custom-theme-dark', 'my-custom-theme-light'] : ['my-custom-theme-light', 'my-custom-theme-dark'])
                : Array.from(new Set(themes.map((theme) => theme + (isDark ? '-dark' : ''))));                      

            // updating the 'chartThemes' grid option will cause the chart to reactively update!
            gridApi.setGridOption('chartThemes', modifiedThemes);
        };

        // update chart themes when example first loads
        updateChartThemes(isInitialModeDark());

        const handleColorSchemeChange = (event) => {
            const { darkMode } = event.detail;
            updateChartThemes(darkMode);
        }

        // listen for user-triggered dark mode changes (not removing listener is fine here!)
        document.addEventListener('color-scheme-change', handleColorSchemeChange);
    /** DARK INTEGRATED END **/

    fetch('https://www.ag-grid.com/example-assets/wide-spread-of-sports.json')
        .then((response) => response.json())
        .then(function (data) {
            gridApi.setGridOption('rowData', data);
        });
});
