'use strict';

import React, { useCallback, useMemo, useRef, useState, StrictMode} from 'react';
import { createRoot } from 'react-dom/client';
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-charts-enterprise';
import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-quartz.css';
import './style.css';



const updateTitle = (api, chartId) => {
    const cellRange = api.getCellRanges()[1];
    if (!cellRange)
        return;
    const columnCount = cellRange.columns.length;
    const rowCount = cellRange.endRow.rowIndex - cellRange.startRow.rowIndex + 1;
    const subtitle = `Using series data from ${columnCount} column(s) and ${rowCount} row(s)`;
    api.updateChart({
        type: 'rangeChartUpdate',
        chartId: chartId,
        chartThemeOverrides: {
            common: {
                subtitle: { text: subtitle },
            },
        },
    });
}



const GridExample = () => {
    const gridRef = useRef();
    const containerStyle = useMemo(() => ({ width: '100%', height: '100%' }), []);
    const gridStyle = useMemo(() => ({height: '100%', width: '100%'}), []);
    const [rowData, setRowData] = useState();
    const [columnDefs, setColumnDefs] = useState([
    { field: 'Month', width: 150, chartDataType: 'category' },
    { field: 'Sunshine (hours)', chartDataType: 'series' },
    { field: 'Rainfall (mm)', chartDataType: 'series' },
]);
    const defaultColDef = useMemo(() => { return { flex: 1 } }, []);
    const popupParent = useMemo(() => { return document.body }, []);
    const chartThemeOverrides = useMemo(() => { return {
    common: {
        title: { enabled: true, text: 'Monthly Weather' },
        subtitle: { enabled: true },
        legend: { enabled: true },
    },
} }, []);


            const onGridReady = useCallback((params) => {
                /** DARK INTEGRATED START **/
    const isInitialModeDark = () => {
            const attr = document.documentElement.getAttribute('data-default-theme');
            return attr ? attr.endsWith('-dark') : false;
        };

        const updateChartThemes = (isDark) => {           
            const themes = ['ag-default', 'ag-material', 'ag-sheets', 'ag-polychroma', 'ag-vivid'];            
            const currentThemes = params.api.getGridOption('chartThemes');                    
            const customTheme = currentThemes && currentThemes.some(theme => theme.startsWith('my-custom-theme'));

            let modifiedThemes = customTheme
                ? (isDark ? ['my-custom-theme-dark', 'my-custom-theme-light'] : ['my-custom-theme-light', 'my-custom-theme-dark'])
                : Array.from(new Set(themes.map((theme) => theme + (isDark ? '-dark' : ''))));                      

            // updating the 'chartThemes' grid option will cause the chart to reactively update!
            params.api.setGridOption('chartThemes', modifiedThemes);
        };

        // update chart themes when example first loads
        updateChartThemes(isInitialModeDark());

        const handleColorSchemeChange = (event) => {
            const { darkMode } = event.detail;
            updateChartThemes(darkMode);
        }

        // listen for user-triggered dark mode changes (not removing listener is fine here!)
        document.addEventListener('color-scheme-change', handleColorSchemeChange);
    /** DARK INTEGRATED END **/

                fetch('https://www.ag-grid.com/example-assets/weather-se-england.json')
                .then(resp => resp.json())
                .then(data => {
    setRowData( data);
});
            }, []);

const onFirstDataRendered = useCallback((params) => {
    const createRangeChartParams = {
        cellRange: {
            rowStartIndex: 0,
            rowEndIndex: 3,
            columns: ['Month', 'Sunshine (hours)'],
        },
        chartType: 'stackedColumn',
        chartContainer: document.querySelector('#myChart'),
    };
    params.api.createRangeChart(createRangeChartParams);
}, [])

   const onChartCreated = useCallback((event) => {
    console.log('Created chart with ID ' + event.chartId);
    updateTitle(gridRef.current.api, event.chartId);
}, [])

   const onChartRangeSelectionChanged = useCallback((event) => {
    console.log('Changed range selection of chart with ID ' + event.chartId);
    updateTitle(gridRef.current.api, event.chartId);
}, [])

    return  (
            <div style={containerStyle}>
                <div className="wrapper">

        <div  style={gridStyle} className={/** DARK MODE START **/document.documentElement.dataset.defaultTheme || 'ag-theme-quartz'/** DARK MODE END **/}>
            <AgGridReact
                ref={gridRef}
                rowData={rowData}
columnDefs={columnDefs}
defaultColDef={defaultColDef}
enableRangeSelection={true}
popupParent={popupParent}
enableCharts={true}
chartThemeOverrides={chartThemeOverrides}
onGridReady={onGridReady}
onFirstDataRendered={onFirstDataRendered}
onChartCreated={onChartCreated}
onChartRangeSelectionChanged={onChartRangeSelectionChanged}
            />
        </div>
    <div id="myChart" className="ag-theme-quartz my-chart"></div>
</div>

            </div>
        );

}

const root = createRoot(document.getElementById('root'));
root.render(<StrictMode><GridExample /></StrictMode>);
window.tearDownExample = () => root.unmount();

