files.styles.css:
html,
body {
    height: 100%;
    width: 100%;
    margin: 0;
    box-sizing: border-box;
    -webkit-overflow-scrolling: touch;
}

html {
    position: absolute;
    top: 0;
    left: 0;
    padding: 0;
    overflow: auto;
}

body {
    padding: 1rem;
    overflow: auto;
}

.custom-element {
    display: flex;
    align-items: center;
    gap: 5px;
    height: 100%;
}

.custom-element button {
    width: 80px;
    padding: 5px !important;
    height: initial !important;
    margin: 0 !important;
}

.custom-element input {
    width: 100px;
    padding: 5px !important;
    height: initial !important;
    margin: 0 !important;
}

.custom-element a {
    line-height: 1rem;
}

.custom-element a:focus-visible {
    outline: 2px solid blue;
    outline-offset: 2px;
    border-radius: 2px;
}

a {
    color: inherit;
}

