.grid-wrapper {
    flex: 2 2 0px;
    min-width: 0;
}

.drop-col {
    padding-left: 10px;
    box-sizing: border-box;
    flex: 1 1 0px;
    display: flex;
    flex-direction: column;
}

.drop-target,
.tile {
    color: white;
}

.drop-target {
    border: 1px solid #888;
    background-color: #99999966;
    padding: 10px;
}

