import { createApp, onBeforeMount, ref, shallowRef } from 'vue';
import { AgGridVue } from 'ag-grid-vue3';
import 'ag-grid-community/styles/ag-grid.css';
import './style.css';
import 'ag-grid-community/styles/ag-theme-quartz.css';



const VueExample = {
    template: `
        <div style="height: 100%">
                <div class="example-wrapper">
      <div class="toolbar">
        <label><input type="checkbox"> Enable suppressMoveWhenRowDragging</label>
      </div>
      <div class="drop-containers">
        <div class="grid-wrapper">
          <ag-grid-vue
            style="width: 100%; height: 100%;"
            :class="themeClass"
            :columnDefs="columnDefs"
            @grid-ready="onGridReady"
            :defaultColDef="defaultColDef"
            :rowClassRules="rowClassRules"
            :rowData="rowData"
            :rowDragManaged="true"></ag-grid-vue>
          </div>
          <div class="drop-col">
            <span id="eDropTarget" class="drop-target">==&gt; Drop to here</span>
            <div class="tile-container"></div>
          </div>
        </div>
      </div>
        </div>
    `,
    components: {
        'ag-grid-vue': AgGridVue,

    },
    setup(props) {
        const columnDefs = ref([{field:"id",
rowDrag:true},{field:"color"},{field:"value1"},{field:"value2"}]);
        const gridApi = shallowRef();
        const defaultColDef = ref({
    filter: true,
    flex: 1,
});
        const rowClassRules = ref(null);
const rowData = ref(null)

        onBeforeMount(() => {
            rowClassRules.value = {
    'red-row': 'data.color == "Red"',
    'green-row': 'data.color == "Green"',
    'blue-row': 'data.color == "Blue"',
};
rowData.value = createRowData()            
        });

        const onGridReady = (params) => {

        gridApi.value = params.api;



    addDropZones(params);
    addCheckboxListener(params);

    };

        return {
            columnDefs,
            gridApi,
            defaultColDef,
rowClassRules,
rowData,
            onGridReady,
            themeClass: /** DARK MODE START **/document.documentElement.dataset.defaultTheme || 'ag-theme-quartz'/** DARK MODE END **/,

        }        
    }
}

window.addCheckboxListener = function addCheckboxListener(params) {
    var checkbox = document.querySelector('input[type=checkbox]');
    checkbox.addEventListener('change', () => {
        params.api.setGridOption('suppressMoveWhenRowDragging', checkbox.checked);
    });
}

window.createRowData = function createRowData() {
    var data = [];
    ['Red', 'Green', 'Blue', 'Red', 'Green', 'Blue', 'Red', 'Green', 'Blue'].forEach((color) => {
        var newDataItem = {
            id: rowIdSequence++,
            color: color,
            value1: Math.floor(Math.random() * 100),
            value2: Math.floor(Math.random() * 100),
        };
        data.push(newDataItem);
    });
    return data;
}

window.createTile = function createTile(data) {
    var el = document.createElement('div');
    el.classList.add('tile');
    el.classList.add(data.color.toLowerCase());
    el.innerHTML =
        '<div class="id">' +
            data.id +
            '</div>' +
            '<div class="value">' +
            data.value1 +
            '</div>' +
            '<div class="value">' +
            data.value2 +
            '</div>';
    return el;
}

window.addDropZones = function addDropZones(params) {
    var tileContainer = document.querySelector('.tile-container');
    var dropZone = {
        getContainer: () => {
            return tileContainer;
        },
        onDragStop: (params) => {
            var tile = createTile(params.node.data);
            tileContainer.appendChild(tile);
        },
    };
    params.api.addRowDropZone(dropZone);
}

var rowIdSequence = 100;

createApp(VueExample)
    .mount("#app")

