function setStyle(element, propertyObject) {
    for (var property in propertyObject) {
        element.style[property] = propertyObject[property];
    }
}
class CustomPinnedRowRenderer  {
     eGui;

    init(params) {
        this.eGui = document.createElement('div');
        setStyle(this.eGui, params.style);
        this.eGui.innerHTML = params.value;
    }

    getGui() {
        return this.eGui;
    }

    refresh() {
        return false;
    }
}

files.main.js:
const columnDefs = [
    {
        field: 'athlete',
        cellRendererSelector: (params) => {
            if (params.node.rowPinned) {
                return {
                    component: CustomPinnedRowRenderer,
                    params: {
                        style: { color: '#5577CC' },
                    },
                };
            } else {
                // rows that are not pinned don't use any cell renderer
                return undefined;
            }
        },
    },
    {
        field: 'country',
        cellRendererSelector: (params) => {
            if (params.node.rowPinned) {
                return {
                    component: CustomPinnedRowRenderer,
                    params: {
                        style: { fontStyle: 'italic' },
                    },
                };
            } else {
                // rows that are not pinned don't use any cell renderer
                return undefined;
            }
        },
    },
    { field: 'sport' },
];

let gridApi;

const gridOptions = {
    defaultColDef: {
        flex: 1,
    },
    columnDefs: columnDefs,
    rowData: null,
    getRowStyle: (params) => {
        if (params.node.rowPinned) {
            return { fontWeight: 'bold' };
        }
    },
    // no rows to pin to start with
    pinnedTopRowData: [
        { athlete: 'TOP 1 (athlete)', country: 'TOP 1 (country)', sport: 'TOP 1 (sport)' },
        { athlete: 'TOP 2 (athlete)', country: 'TOP 2 (country)', sport: 'TOP 2 (sport)' },
    ],
    pinnedBottomRowData: [
        { athlete: 'BOTTOM 1 (athlete)', country: 'BOTTOM 1 (country)', sport: 'BOTTOM 1 (sport)' },
        { athlete: 'BOTTOM 2 (athlete)', country: 'BOTTOM 2 (country)', sport: 'BOTTOM 2 (sport)' },
    ],
};

function createData(count, prefix) {
    var result = [];
    for (var i = 0; i < count; i++) {
        result.push({
            athlete: prefix + ' Athlete ' + i,
            age: prefix + ' Age ' + i,
            country: prefix + ' Country ' + i,
            year: prefix + ' Year ' + i,
            date: prefix + ' Date ' + i,
            sport: prefix + ' Sport ' + i,
        });
    }
    return result;
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 

    fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
        .then((response) => response.json())
        .then((data) => gridApi.setGridOption('rowData', data));
});
