let gridApi;

const gridOptions = {
    columnDefs: [
        { headerName: 'Index', valueGetter: 'node.rowIndex', minWidth: 100 },
        { field: 'athlete', minWidth: 150 },
        { field: 'country', minWidth: 150 },
        { field: 'year' },
        { field: 'sport', minWidth: 120 },
        { field: 'gold' },
        { field: 'silver' },
        { field: 'bronze' },
    ],

    defaultColDef: {
        flex: 1,
        minWidth: 80,
        sortable: false,
    },

    rowModelType: 'serverSide',

    maxBlocksInCache: 0,
    cacheBlockSize: 50,
};

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 

    fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
        .then((response) => response.json())
        .then(function (data) {
            // setup the fake server with entire dataset
            var fakeServer = createFakeServer(data);

            // create datasource with a reference to the fake server
            var datasource = createServerSideDatasource(fakeServer);

            // register the datasource with the grid
            gridApi.setGridOption('serverSideDatasource', datasource);

            const initialData = fakeServer.getData({
                startRow: 0,
                endRow: 100,
            });

            gridApi.applyServerSideRowData({
                successParams: {
                    rowData: initialData.rows,
                    rowCount: initialData.lastRow,
                },
            });
        });
});

function createServerSideDatasource(server) {
    return {
        getRows: (params) => {
            console.log(
                '[Datasource] - rows requested by grid: startRow = ' +
                    params.request.startRow +
                    ', endRow = ' +
                    params.request.endRow
            );

            // get data for request from our fake server
            var response = server.getData(params.request);

            // simulating real server call with a 500ms delay
            setTimeout(() => {
                if (response.success) {
                    // supply rows for requested block to grid
                    params.success({ rowData: response.rows, rowCount: response.lastRow });
                } else {
                    params.fail();
                }
            }, 1000);
        },
    };
}

function createFakeServer(allData) {
    return {
        getData: (request) => {
            // in this simplified fake server all rows are contained in an array
            var requestedRows = allData.slice(request.startRow, request.endRow);

            return {
                success: true,
                rows: requestedRows,
                lastRow: allData.length,
            };
        },
    };
}
