const columnDefs = [
    {
        headerName: 'Athlete',
        field: 'athlete',
        tooltipComponentParams: { color: '#55AA77' },
        tooltipField: 'country',
        headerTooltip: 'Tooltip for Athlete Column Header',
    },
    {
        field: 'age',
        tooltipValueGetter: (p) => 'Create any fixed message, e.g. This is the Athleteâ€™s Age ',
        headerTooltip: 'Tooltip for Age Column Header',
    },
    {
        field: 'year',
        tooltipValueGetter: (p) => 'This is a dynamic tooltip using the value of ' + p.value,
        headerTooltip: 'Tooltip for Year Column Header',
    },
    {
        field: 'sport',
        tooltipValueGetter: () => 'Tooltip text about Sport should go here',
        headerTooltip: 'Tooltip for Sport Column Header',
    },
];

let gridApi;

const gridOptions = {
    defaultColDef: {
        flex: 1,
        minWidth: 100,
        tooltipComponent: CustomTooltip,
    },

    tooltipShowDelay: 0,
    tooltipHideDelay: 2000,

    // set rowData to null or undefined to show loading panel by default
    rowData: null,
    columnDefs: columnDefs,
};

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', () => {
    const gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 

    fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
        .then((response) => response.json())
        .then((data) => {
            gridApi.setGridOption('rowData', data);
        });
});
