externalEventHandlers.body:
function onBtStartEditing(key, char, pinned) {
    this.gridApi.setFocusedCell(0, 'lastName', pinned);
    this.gridApi.startEditingCell({
        rowIndex: 0,
        colKey: 'lastName',
        rowPinned: pinned,
        key: key,
        charPress: char
    });
}

function onBtWhich() {
    var cellDefs = this.gridApi.getEditingCells();
    if (cellDefs.length > 0) {
        var cellDef = cellDefs[0];
        console.log('editing cell is: row = ' + cellDef.rowIndex + ', col = ' + cellDef.column.getId() + ', floating = ' + cellDef.rowPinned);
    } else {
        console.log('no cells are editing');
    }
}

