utils:
const athleteColumn = {
    headerName: 'Athlete',
    valueGetter: function (params) {
        return params.data.athlete;
    }
};

function getColDefsMedalsIncluded() {
    return [
        athleteColumn,
        {
            colId: 'myAgeCol',
            headerName: 'Age',
            valueGetter: function (params) {
                return params.data.age;
            }
        },
        {
            headerName: 'Country',
            headerClass: 'country-header',
            valueGetter: function (params) {
                return params.data.country;
            }
        },
        { field: 'sport' },
        { field: 'year' },
        { field: 'date' },
        { field: 'gold' },
        { field: 'silver' },
        { field: 'bronze' },
        { field: 'total' }
    ];
}

