properties.value:
[{
            headerName: "Top Level Column Group",
            children: [
                {
                    headerName: "Group A",
                    children: [
                        {
                            field: "athlete",
                            minWidth: 200
                        },
                        {
                            field: "age",
                            cellClass: "twoDecimalPlaces",
                            cellClassRules: {
                                greenBackground: function (params) {
                                    return params.value < 23;
                                },
                                redFont: function (params) {
                                    return params.value < 20;
                                }
                            }
                        },
                        {
                            field: "country",
                            minWidth: 200,
                            cellClassRules: {
                                redFont: function (params) {
                                    return params.value === "United States";
                                }
                            }
                        },
                        {
                            headerName: "Group",
                            valueGetter: "data.country.charAt(0)",
                            cellClassRules: {
                                boldBorders: function (params) {
                                    return params.value === "U";
                                }
                            },
                            cellClass: [
                                "redFont",
                                "greenBackground"
                            ]
                        },
                        {
                            field: "year",
                            cellClassRules: {
                                notInExcel: function (params) {
                                    return true;
                                }
                            }
                        }
                    ]
                },
                {
                    headerName: "Group B",
                    children: [
                        {
                            field: "date",
                            minWidth: 150,
                            cellClass: "dateFormat",
                            valueGetter: function (params) {
                                var val = params.data.date;
                                if (val.indexOf("/") < 0) {
                                    return val;
                                }
                                var split = val.split("/");
                                return split[2] + "-" + split[1] + "-" + split[0];
                            }
                        },
                        {
                            field: "sport",
                            minWidth: 150
                        },
                        {
                            field: "gold",
                            cellClassRules: {
                                boldBorders: function (params) {
                                    return params.value > 2;
                                }
                            }
                        },
                        {
                            field: "silver",
                            cellClass: "textFormat"
                        },
                        { field: "bronze" },
                        { field: "total" }
                    ]
                }
            ]
        }]

