utils:
function colourCellRenderer(params) {
    if (!params.value || params.value === '(Select All)') {
        return params.value;
    }
    return `<div style="background-color: ${ params.value.toLowerCase() }; ${ FIXED_STYLES }"></div>${ params.value }`;
}
