function onFirstDataRendered(params) {
    var cellRange = {
        rowStartIndex: 0,
        rowEndIndex: 20,
        columns: ['bronze']
    };
    var createRangeChartParams = {
        cellRange: cellRange,
        chartType: 'histogram'
    };
    params.api.createRangeChart(createRangeChartParams);
}

