externalEventHandlers.body:
function onBtRemove() {
    var selectedRows = this.gridApi.getSelectedNodes();
    if (!selectedRows || selectedRows.length === 0) {
        return;
    }
    var selectedRow = selectedRows[0];
    var indexToRemove = window.rowDataServerSide.indexOf(selectedRow.data);
    if (indexToRemove >= 0) {
        window.rowDataServerSide.splice(indexToRemove, 1);
    }
    this.gridApi.refreshServerSideStore();
}

function onBtAdd() {
    var selectedRows = this.gridApi.getSelectedNodes();
    if (!selectedRows || selectedRows.length === 0) {
        return;
    }
    var selectedRow = selectedRows[0];
    window.rowDataServerSide.splice(selectedRow.rowIndex, 0, { athlete: 'New Item' + newItemCount });
    newItemCount++;
    this.gridApi.refreshServerSideStore();
}

utils:
function createMyDataSource(data) {
    window.rowDataServerSide = data;
    const dataSource = {
        getRows: function (params) {
            setTimeout(function () {
                var rowsThisPage = data.slice(params.request.startRow, params.request.endRow);
                params.success({
                    rowData: rowsThisPage,
                    rowCount: window.rowDataServerSide.length
                });
            }, 500);
        }
    };
    return dataSource;
}
