function createRowData() {
    const rowData = [];
    for (let i = 0; i < 20; i++) {
        rowData.push({
            a: Math.floor((i + 323) * 25435 % 10000),
            b: Math.floor((i + 323) * 23221 % 10000),
            c: Math.floor((i + 323) * 468276 % 10000),
            d: 0,
            e: 0
        });
    }
    return rowData;
}
