externalEventHandlers.body:
function startUpdates() {
    if (updating) {
        return;
    }
    updating = true;
    this.update();
    setInterval(this.update, 500);
}

globals:
function getData() {
    data.shift();
    while (data.length < 20) {
        data.push({
            time: new Date(lastTime += 1000),
            voltage: 1.1 + Math.random() / 2
        });
    }
    return data;
}
