properties.value:
{

        data: data,
        title: { text: 'World Population Over Time' },
        subtitle: { text: 'log scale' },
        series: [{
                type: 'line',
                xKey: 'year',
                yKey: 'population'
            }],
        axes: [
            {
                type: 'log',
                position: 'left',
                title: {
                    enabled: true,
                    text: 'Population'
                },
                label: {
                    format: ',.0f',
                    fontSize: 10
                }
            },
            {
                type: 'number',
                position: 'bottom',
                title: {
                    enabled: true,
                    text: 'Year'
                },
                label: { fontSize: 10 }
            }
        ],
        legend: { enabled: false }
    }

externalEventHandlers.body:
function useNumberAxis() {
    options.subtitle = { text: 'linear scale' };
    options.axes[0] = {
        type: 'number',
        position: 'left',
        title: {
            enabled: true,
            text: 'Population'
        },
        label: {
            format: ',.0f',
            fontSize: 10
        }
    };

}

function useLogAxis() {
    options.subtitle = { text: 'log scale' };
    options.axes[0] = {
        type: 'log',
        position: 'left',
        title: {
            enabled: true,
            text: 'Population'
        },
        label: {
            format: ',.0f',
            fontSize: 10
        }
    };

}
