function onBtUpperNames() {
    const columnDefs = [
        { field: 'athlete' },
        { field: 'age' },
        { field: 'country' },
        { field: 'year' },
        { field: 'date' },
        { field: 'sport' },
        { field: 'gold' },
        { field: 'silver' },
        { field: 'bronze' },
        { field: 'total' }
    ];
    columnDefs.forEach(function (c) {
        c.headerName = c.field.toUpperCase();
    });
    this.gridApi.setColumnDefs(columnDefs);
}

