externalEventHandlers.body:
function onBtnExport() {
    var params = getParams();
    if (params.suppressQuotes || params.columnSeparator) {
        alert('NOTE: you are downloading a file with non-standard quotes or separators - it may not render correctly in Excel.');
    }
    this.gridApi.exportDataAsCsv(params);
}

utils:
function getValue(inputSelector) {
    var text = document.querySelector(inputSelector).value;
    switch (text) {
    case 'string':
        return 'Here is a comma, and a some "quotes". You can see them using the\n' + 'api.getDataAsCsv() button but they will not be visible when the downloaded\n' + 'CSV file is opened in Excel because string content passed to\n' + 'prependContent and appendContent is not escaped.';
    case 'array':
        return [
            [],
            [{
                    data: {
                        value: 'Here is a comma, and a some "quotes".',
                        type: 'String'
                    }
                }],
            [{
                    data: {
                        value: 'They are visible when the downloaded CSV file is opened in Excel because custom content is properly escaped (provided that suppressQuotes is not set to true)',
                        type: 'String'
                    }
                }],
            [
                {
                    data: {
                        value: 'this cell:',
                        type: 'String'
                    },
                    mergeAcross: 1
                },
                {
                    data: {
                        value: 'is empty because the first cell has mergeAcross=1',
                        type: 'String'
                    }
                }
            ],
            []
        ];
    case 'none':
        return;
    default:
        return text;
    }
}

function getParams() {
    return {
        prependContent: getValue('#prependContent'),
        appendContent: getValue('#appendContent'),
        suppressQuotes: undefined,
        columnSeparator: undefined
    };
}
