externalEventHandlers.body:
function onBtDuplicate() {
    var api = this.gridApi;
    var selectedRows = api.getSelectedRows();
    if (!selectedRows || selectedRows.length === 0) {
        console.log('No rows selected!');
        return;
    }
    var newItems = [];
    selectedRows.forEach(function (selectedRow) {
        idCounter++;
        var newItem = createDataItem(idCounter, selectedRow.name, selectedRow.distro, selectedRow.laptop, selectedRow.city, selectedRow.value);
        newItems.push(newItem);
    });
    timeOperation('Duplicate', function () {
        api.applyTransaction({ add: newItems });
    });
}

function onBtUpdate() {
    var api = this.gridApi;
    var selectedRows = api.getSelectedRows();
    if (!selectedRows || selectedRows.length === 0) {
        console.log('No rows selected!');
        return;
    }
    var updatedItems = [];
    selectedRows.forEach(function (oldItem) {
        var newValue = Math.floor(Math.random() * 100) + 10;
        var newItem = createDataItem(oldItem.id, oldItem.name, oldItem.distro, oldItem.laptop, oldItem.city, newValue);
        updatedItems.push(newItem);
    });
    timeOperation('Update', function () {
        api.applyTransaction({ update: updatedItems });
    });
}

function onBtDelete() {
    var api = this.gridApi;
    var selectedRows = api.getSelectedRows();
    if (!selectedRows || selectedRows.length === 0) {
        console.log('No rows selected!');
        return;
    }
    timeOperation('Delete', function () {
        api.applyTransaction({ remove: selectedRows });
    });
}

function onBtUpdateModel() {
    var api = this.gridApi;
    timeOperation('Update Model', function () {
        api.refreshClientSideRowModel('filter');
    });
}

utils:
function myAggFunc(params) {
    aggCallCount++;
    var total = 0;
    for (var i = 0; i < params.values.length; i++) {
        total += params.values[i];
    }
    return total;
}

function myComparator(a, b) {
    compareCallCount++;
    return a < b ? -1 : 1;
}

function getMyFilter() {
    class MyFilter {
        init(params) {
            this.filterParams = params;
            this.filterValue = null;
            this.eGui = document.createElement('div');
            this.eGui.innerHTML = '<div>Greater Than: <input type="text"/></div>';
            this.eInput = this.eGui.querySelector('input');
            var that = this;
            this.eInput.addEventListener('input', function () {
                that.getValueFromInput();
                params.filterChangedCallback();
            });
        }
        getGui() {
            return this.eGui;
        }
        getValueFromInput() {
            var value = parseInt(this.eInput.value);
            this.filterValue = isNaN(value) ? null : value;
        }
        setModel(model) {
            this.eInput.value = model.value;
            this.getValueFromInput();
        }
        getModel() {
            return { value: this.eInput.value };
        }
        isFilterActive() {
            return this.filterValue !== null;
        }
        doesFilterPass(params) {
            filterCallCount++;
            const {api, colDef, column, columnApi, context} = this.filterParams;
            const {node} = params;
            const value = this.filterParams.valueGetter({
                api,
                colDef,
                column,
                columnApi,
                context,
                data: node.data,
                getValue: field => node.data[field],
                node
            });
            return value > (this.filterValue || 0);
        }
    }
    return MyFilter;
}

function timeOperation(name, operation) {
    aggCallCount = 0;
    compareCallCount = 0;
    filterCallCount = 0;
    var start = new Date().getTime();
    operation();
    var end = new Date().getTime();
    console.log(name + ' finished in ' + (end - start) + 'ms, aggCallCount = ' + aggCallCount + ', compareCallCount = ' + compareCallCount + ', filterCallCount = ' + filterCallCount);
}

function getData() {
    var myRowData = [];
    for (var i = 0; i < 10000; i++) {
        var name = 'Mr ' + randomLetter().toUpperCase() + ' ' + randomLetter().toUpperCase() + randomLetter() + randomLetter() + randomLetter() + randomLetter();
        var city = CITIES[i % CITIES.length];
        var distro = LINUX_DISTROS[i % LINUX_DISTROS.length] + ' v' + Math.floor(Math.random() * 100 + 1) / 10;
        var university = LAPTOPS[i % LAPTOPS.length];
        var value = Math.floor(Math.random() * 100) + 10;
        idCounter++;
        myRowData.push(createDataItem(idCounter, name, distro, university, city, value));
    }
    return myRowData;
}

function createDataItem(id, name, distro, laptop, city, value) {
    return {
        id: id,
        name: name,
        city: city,
        distro: distro,
        laptop: laptop,
        value: value
    };
}

onGridReady:
{
    params.api.setFilterModel({ value: { value: '50' } });
    timeOperation('Initialisation', function () {
        params.api.setRowData(getData());
    });
    params.api.getDisplayedRowAtIndex(2).setExpanded(true);
    params.api.getDisplayedRowAtIndex(4).setExpanded(true);
}
