function updateFirstRow() {
    var firstRow = this.gridApi.getDisplayedRowAtIndex(0);
    if (firstRow) {
        var firstRowData = firstRow.data;
        firstRowData['col1'] += 'X';
        this.gridApi.applyTransaction({ update: [firstRowData] });
    }
}

