externalEventHandlers.body:
function irelandAndUk() {
    var countryFilterComponent = this.gridApi.getFilterInstance('country');
    countryFilterComponent.setModel({
        values: [
            'Ireland',
            'Great Britain'
        ]
    });
    this.gridApi.onFilterChanged();
}

function clearCountryFilter() {
    var countryFilterComponent = this.gridApi.getFilterInstance('country');
    countryFilterComponent.setModel(null);
    this.gridApi.onFilterChanged();
}

function endingStan() {
    var countryFilterComponent = this.gridApi.getFilterInstance('country');
    var countriesEndingWithStan = countryFilterComponent.getValues().filter(function (value) {
        return value.indexOf('stan') === value.length - 4;
    });
    countryFilterComponent.setModel({ values: countriesEndingWithStan });
    this.gridApi.onFilterChanged();
}

function printCountryModel() {
    var countryFilterComponent = this.gridApi.getFilterInstance('country');
    var model = countryFilterComponent.getModel();
    if (model) {
        console.log('Country model is: ' + JSON.stringify(model));
    } else {
        console.log('Country model filter is not active');
    }
}

function sportStartsWithS() {
    var sportsFilterComponent = this.gridApi.getFilterInstance('sport');
    sportsFilterComponent.setModel({
        type: 'startsWith',
        filter: 's'
    });
    this.gridApi.onFilterChanged();
}

function sportEndsWithG() {
    var sportsFilterComponent = this.gridApi.getFilterInstance('sport');
    sportsFilterComponent.setModel({
        type: 'endsWith',
        filter: 'g'
    });
    this.gridApi.onFilterChanged();
}

function sportsCombined() {
    var sportsFilterComponent = this.gridApi.getFilterInstance('sport');
    sportsFilterComponent.setModel({
        condition2: {
            type: 'endsWith',
            filter: 'g'
        },
        operator: 'AND',
        condition1: {
            type: 'startsWith',
            filter: 's'
        }
    });
    this.gridApi.onFilterChanged();
}

function ageBelow25() {
    var ageFilterComponent = this.gridApi.getFilterInstance('age');
    ageFilterComponent.setModel({
        type: 'lessThan',
        filter: 25,
        filterTo: null
    });
    this.gridApi.onFilterChanged();
}

function ageAbove30() {
    var ageFilterComponent = this.gridApi.getFilterInstance('age');
    ageFilterComponent.setModel({
        type: 'greaterThan',
        filter: 30,
        filterTo: null
    });
    this.gridApi.onFilterChanged();
}

function ageBelow25OrAbove30() {
    var ageFilterComponent = this.gridApi.getFilterInstance('age');
    ageFilterComponent.setModel({
        condition1: {
            type: 'greaterThan',
            filter: 30,
            filterTo: null
        },
        operator: 'OR',
        condition2: {
            type: 'lessThan',
