externalEventHandlers.body:
function cbFloatingRows() {
    var show = document.getElementById('floating-rows').checked;
    if (show) {
        this.gridApi.setPinnedTopRowData([
            createRow(999),
            createRow(998)
        ]);
        this.gridApi.setPinnedBottomRowData([
            createRow(997),
            createRow(996)
        ]);
    } else {
        this.gridApi.setPinnedTopRowData();
        this.gridApi.setPinnedBottomRowData();
    }
}

