externalEventHandlers.body:
function clearFilter() {
    this.gridApi.setFilterModel(null);
    setTitle('All Medals by Country');
}

function filterUKAndIrelandBoxing() {
    this.gridApi.setFilterModel({
        country: {
            type: 'set',
            values: [
                'Ireland',
                'Great Britain'
            ]
        },
        sport: {
            type: 'set',
            values: ['Boxing']
        }
    });
    setTitle('UK and Ireland - Boxing');
}

