function addCheckboxListener(params) {
    var checkbox = document.querySelector('input[type=checkbox]');
    checkbox.addEventListener('change', function () {
        params.api.setSuppressMoveWhenRowDragging(checkbox.checked);
    });
}

function createRowData() {
    var data = [];
    [
        'Red',
        'Green',
        'Blue',
        'Red',
        'Green',
        'Blue',
        'Red',
        'Green',
        'Blue'
    ].forEach(function (color) {
        var newDataItem = {
            id: rowIdSequence++,
            color: color,
            value1: Math.floor(Math.random() * 100),
            value2: Math.floor(Math.random() * 100)
        };
        data.push(newDataItem);
    });
    return data;
}

function createTile(data) {
    var el = document.createElement('div');
    el.classList.add('tile');
    el.classList.add(data.color.toLowerCase());
    el.innerHTML = '<div class="id">' + data.id + '</div>' + '<div class="value">' + data.value1 + '</div>' + '<div class="value">' + data.value2 + '</div>';
    return el;
}

function addDropZones(params) {
    var tileContainer = document.querySelector('.tile-container');
    var dropZone = {
        getContainer: function () {
            return tileContainer;
        },
        onDragStop: function (params) {
            var tile = createTile(params.node.data);
            tileContainer.appendChild(tile);
        }
    };
    params.api.addRowDropZone(dropZone);
}
