function onBtNewPalmOil() {
    const transaction = {
        route: ['Palm Oil'],
        add: [createOneTrade()]
    };
    const res = this.gridApi.applyServerSideTransaction(transaction);
    console.log('New Palm Oil, result = ' + (res && res.status));
}

