function onBtNewWoolAmber() {
    const transactions = [];
    transactions.push({
        route: ['Wool'],
        add: [createOneTrade()]
    });
    transactions.push({
        route: ['Amber'],
        add: [createOneTrade()]
    });
    const api = this.gridApi;
    transactions.forEach(function (tx) {
        const res = api.applyServerSideTransaction(tx);
        console.log('New ' + tx.route[0] + ', result = ' + (res && res.status));
    });
}

