function createFakeServer(fakeServerData) {
    const fakeServer = {
        getData: function (request) {
            function extractRowsFromData(groupKeys, data) {
                if (groupKeys.length === 0) {
                    return data.map(function (d) {
                        return {
                            group: !!d.underlings,
                            employeeId: d.employeeId + '',
                            employeeName: d.employeeName,
                            employmentType: d.employmentType,
                            startDate: d.startDate
                        };
                    });
                }
                var key = groupKeys[0];
                for (var i = 0; i < data.length; i++) {
                    if (data[i].employeeName === key) {
                        return extractRowsFromData(groupKeys.slice(1), data[i].underlings.slice());
                    }
                }
            }
            return extractRowsFromData(request.groupKeys, fakeServerData);
        }
    };
    return fakeServer;
}

function createServerSideDatasource(fakeServer) {
    const dataSource = {
        getRows: function (params) {
            console.log('ServerSideDatasource.getRows: params = ', params);
            var request = params.request;
            var allRows = fakeServer.getData(request);
            var doingInfinite = request.startRow != null && request.endRow != null;
            var result = doingInfinite ? {
                rowData: allRows.slice(request.startRow, request.endRow),
                rowCount: allRows.length
            } : { rowData: allRows };
            console.log('getRows: result = ', result);
            setTimeout(function () {
                params.success(result);
            }, 500);
        }
    };
    return dataSource;
}

data.callback:
{
    var fakeServer = createFakeServer(data);
    var datasource = createServerSideDatasource(fakeServer);
    params.api.setServerSideDatasource(datasource);
}
