function tooltipRenderer(params) {
    const {yValue, context} = params;
    return `<div class='sparkline-tooltip'>
            <div class='tooltip-title'>${ context.data.symbol }</div>
            <div class='tooltip-content'>${ yValue }</div>
         </div>`;
}
